import React from 'react';

const TextInput = () => {
    const [text, setText] = useRecoilState(textState);

    const onChange = (event) => {
      setText(event.target.value);
    }

    return (
      <div>
       <input type="text" value={text} onChange={(e) => onChange(e)} />
       <br />
       Echo: {text}
      </div>
    )
  }

  export default TextInput;
